# coding=utf-8

# https://www.hackerrank.com/challenges/the-minion-game/problem

def minion_game(s):
    vowels = list("AEIOU")
    consonants = [chr(c) for c in range(ord('B'), ord('Z')+1) if chr(c) not in vowels]

    sum_of_kevin = 0
    sum_of_stuart = 0
    for i in range(len(s)):
        if s[i] in vowels:
            sum_of_kevin += len(s) - i
        else:
            sum_of_stuart += len(s) - i


    if sum_of_kevin > sum_of_stuart :
        print(f"Kevin {sum_of_kevin}")
    elif sum_of_kevin < sum_of_stuart :
        print(f"Stuart {sum_of_stuart}")
    else :
        print("Draw")


if __name__ == '__main__':

    s = "" #input()
    # Stuart 40454713

#    s = "BANANA"
    minion_game(s)


